package oops;
class Human{
	int age;
	String name;	
}
public class Before_Encapsulation {
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Human obj=new Human();
		obj.age=23;
		obj.name="sudheer";
		System.out.println(obj.age);
		System.out.println(obj.name);
	
	
	}

}
