package operatos;

public class Logical_operators {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=5,b=6,c=7;
		//logical AND->&&
		System.out.println("(a<b) && (b<c):" +((a<b) && (b<c)) );
		//logical OR->||
		System.out.println("(a>b) || (b<c):" +((a>b) || (b<c)) );
		//logical NOT->!
		System.out.println("!(b>c):" + !(b>c));
		

	}

}
