package org.tns.capgemini.c2tc.operatos;

public class AssignmentOperators {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=10;
		a+=5;
	    System.out.println(+a);
		a-=3;
		System.out.println(+a);
		a*=3;
		System.out.println(+a);
		a/=3;
		System.out.println(+a);
		a%=3;
		System.out.println(+a);
		

	}

}
